firestore.rules:
rules_version = '2';

service cloud.firestore {
  match /databases/{database}/documents {

    // User profiles are readable by anyone, but only writable by the user themselves.
    match /users/{userId} {
      allow read;
      allow write: if request.auth.uid == userId;
    }

    // Companies are readable by anyone, but only writable by the owner.
    match /companies/{companyId} {
      allow read;
      allow write: if get(/databases/$(database)/documents/companies/$(companyId)).data.ownerId == request.auth.uid;
    }
  }
}
